    if (this.wider) {
      this._r.addClass(this._el.nativeElement, 'wider');
    }
    if (this.narrower) {
      this._r.addClass(this._el.nativeElement, 'narrower');
    }
    if (this.class) {
      this.class.split(' ').forEach((element: any) => {
        this._r.addClass(this._el.nativeElement, element);
      });
    }
  }
}
